(()=>{const t=document.getElementById("films"),e=e=>{const n=document.createElement("article"),r=document.createElement("div");n.classList.add("movie"),r.classList.add("divMovie"),n.innerHTML=`<img src="${e.poster}" alt="${e.title}">`,r.innerHTML=`\n            <h3>${e.title} - (${e.year})</h3>\n            <p><strong>Director:</strong> ${e.director}</p>\n            <p><strong>Duración:</strong> ${e.duration}</p>\n            <p><strong>Género:</strong> ${e.genre.join(", ")}</p>\n            <p><strong>Rate:</strong> ${e.rate}</p>\n        `,n.appendChild(r),t.appendChild(n)};$.get("https://students-api.up.railway.app/movies",(t=>t.map(e))).fail((()=>alert("Error al traer las peliculas")))})();